import React, { useState } from "react";
import "./styles.css";
import AllTheThings from "./components/AllTheThings";
import MyShoppingCart from "./components/MyShoppingCart";
import Form from "./components/Form";
import productsArr from "./products";

export default function App() {
  const [products, setProducts] = useState(productsArr);
  const [cart, setCart] = useState([]);

  // Task 1: Implement cart removal and adding, implement product adding.

  function addToCart(product) {
    const newCart = cart.map((c) => {
      return {...c}
    }); 
    console.info("new cart >>> ", newCart);
    newCart.push(product);
    setCart(newCart);
  }

  function removeFromCart(productName) {
    console.info("product in remove cart >>> ", productName);
    const newCart = cart.map((c) => {
      if(c.name === productName) ;
      else return {...c};
    })
    console.info("new cart >>> ", newCart);
    // setCart(newCart);
  } 


  return (
    <>
      <h1>Big Time Shopping</h1>
      <div className="App">
        <div className="AllTheThings">
          {/* Task 2: Render addProduct, and pass addProduct function as props */}
          <AllTheThings products={products} addToCart={addToCart} />
        </div>

        <div className="MyShoppingCart">
          {/* Task 3: Render AllTheThings and MyShoppingCart, and pass the functions addToCart and remove from cart as props */}

          {/* <MyShoppingCart cart={cart}/> */}

          {!!cart ? (<MyShoppingCart cart={cart} removeFromCart={removeFromCart} />) :(<MyShoppingCart />)}
        </div>
      </div>
    </>
  );
}
