import React, { useRef, useState } from "react";

function Form(props) {

  const {addToProducts} = props; 

  // Task 6: Create useState hooks for productName and price
  const inputRefName = useRef();
  const inputRefPrice = useRef();
  const inputRefDescription = useRef();

  const [name, setName] = useState("");
  const [price, setPrice] = useState(0);
  const [description, setDescription] = useState("");

  let isDisabled = true;
  if(name.length > 1 && price > 0 && description.length > 1) isDisabled = false;    

  // Task 7: Create a function to handle the form submission
  function handleSubmit() {
    setName(inputRefName.current.value);
    setPrice(inputRefPrice.current.value);
    setDescription(inputRefDescription.current.value);

    const product = {
      name: name,
      price: price,
      description: description
    }

    console.info("product in form >>> ", product);

    addToProducts(product);

  }

  return (
    // Task 8: Add input fields and a submit button
    <>
      <input 
        type="text" 
        placeholder="Item Name" 
        ref={inputRefName}
      ></input>
      <input 
        type="number" 
        placeholder="Item Price" 
        ref={inputRefPrice}
      ></input>
      <input
        type="text"
        placeholder="Item Description"
        ref={inputRefDescription}
      ></input>
      <button type="submit" onClick={handleSubmit}>
        Display in Shop
      </button>
    </>
  );
}

export default Form;
