import React, { useState } from 'react';

function MyShoppingCart(props) {

  const {cart, removeFromCart} = props;

  function handleClick(event) {
    const clickedProduct = event.target.innerHTML;
    const productArr = clickedProduct.split("-").map(function(str) {
      return str.trim(); 
    });
    removeFromCart(productArr[0]);
  }

  return (
    <div className="MyShoppingCart">
      <h2 data-testid="my-shopping-cart-header">Your Cart!</h2>
      {/* Task 5: Render cart items in MyShoppingCart component,
       Add onClick handler to remove the item */}
      {props.cart.map((item, index) => (
        <div
          key={index}
          data-testid="cart-item"
          // Add onClick handler to remove the item
          onClick={handleClick}
        >
          {item.name} - ${item.price}
        </div>
      ))}
    </div>
  );
}

export default MyShoppingCart;
