import React, { useRef, useState } from "react";

function Form(props) {
  // Task 6: Create useState hooks for productName and price
  const inputRefName = useRef;
  const inputRefPrice = useRef();
  const inputRefDescription = useRef();

  const [name, setName] = useState("");
  const [price, setPrice] = useState(0);
  const [description, setDescription] = useState("");

  // Task 7: Create a function to handle the form submission
  function handleSubmit() {
    setName(inputRefName.current.value);
    setPrice(inputRefPrice.current.value);
    setDescription(inputRefDescription.current.value);

    console.info("name >> ", name);
    console.info("price >> ", price);
    console.info("price >> ", price);
  }

  return (
    // Task 8: Add input fields and a submit button
    <>
      <input type="text" placeholder="Item Name"></input>
      <input type="number" placeholder="Item Price"></input>
      <input type="text" placeholder="Item Description"></input>
      <button type="submit">Display in Shop</button>
    </>
  );
}

export default Form;
