import React from 'react';

function AllTheThings(props) {

  const {products, addToCart} = props;

  function handleClick(event) {
    const clickedProduct = event.currentTarget.children;
    const product = {
      name: clickedProduct[0].innerHTML,
      price: clickedProduct[1].innerHTML,
      description: clickedProduct[2].innerHTML
    }
    console.info("product >>> ", product.currentTarget.children);


  }

  return (
    <div className="AllTheThings">
      <h2 data-testid="all-the-things-header">Put these in your cart!</h2>
      {/* Task 4: Render products in AllTheThings component */}
      {products.map((product) => (
        <div className='products' onClick={handleClick}>
          <span>{product.name}</span>
          <span>{product.price}</span>
          <span>{product.description}</span>
        </div>
      ))}
    </div>
  );
}

export default AllTheThings;
