import React, { useReducer, useState } from "react";
import "./App.css";
import Column from "./components/Column";

const titles = ["What went well", "What can be improved", "Action items"];
// Task 1: Create initial state
const initialState = {
  // Code goes here
  items: [],
  votes: []
};

let itemPrompt = "";

// Task 2: Define reducer function
function reducer(state, action) {
  switch (action.type) {
    // Code goes here
    case "ADD ITEM":
      console.info(">>>> action: ", action.value);
      console.info(">>>> state: ", state);
      itemPrompt = prompt(`Add item to "${action.value}" column.`, "");
      if (!state.title) {
        state.title = action.value;
        state.items.push(itemPrompt);
      } else {
        state.items.push(itemPrompt);
      }
      return state;

    case titles[1]:
      itemPrompt = prompt(`Add item to "${action}" column.`, "");
      if (!!itemPrompt) state[1].items.push(itemPrompt);
      return state;

    case titles[2]:
      itemPrompt = prompt(`Add item to "${action}" column.`, "");
      if (!!itemPrompt) state[2].items.push(itemPrompt);
      return state;

    case "DELETE":
      break;

    default:
      return state;
  }
}

function App() {
  // Task 3: Set up state with the use reducer hook
  // code goes here
  const [obj, dispatch] = useReducer(reducer, initialState);
  console.info("item in app >>> ", obj);
  return (
    <div className="App">
      <h1>Retrospective Board</h1>
      <div className="columns">
        {/* Task 4: Render column components and pass state,dispatch down */}
        {/* Code goes here*/}
        <Column
          key={titles[0]}
          columnKey={0}
          title={titles[0]}
          items={obj.items}
          dispatch={dispatch}
        />
        <Column
          key={titles[1]}
          columnKey={1}
          title={titles[0]}
          items={obj.items}
          dispatch={dispatch}
        />
        <Column
          key={titles[2]}
          columnKey={0}
          title={titles[0]}
          items={obj.items}
          dispatch={dispatch}
        />
      </div>
    </div>
  );
}

export default App;
