import React, { useReducer } from "react";
import "./App.css";
import Column from "./components/Column";

const titles = ["What went well", "What can be improved", "Action items"];
// const items = [];

// Task 1: Create initial state
const initialState = [
  // Code goes here
  {title: titles[0], items:[]},
  {title: titles[1], items:[]},
  {title: titles[2], items:[]}
];

let itemPrompt = "";

// Task 2: Define reducer function
function reducer(state, action) {
  switch (action) {
    // Code goes here
    case titles[0]:
      itemPrompt = prompt(`Add item to "${action}" column.`, "");
      if(!!itemPrompt) state[0].items.push(itemPrompt);
      return state; 

    case titles[1]:
      itemPrompt = prompt(`Add item to "${action}" column.`, "");
      if(!!itemPrompt) state[1].items.push(itemPrompt);
      return state; 

    case titles[2]:
      itemPrompt = prompt(`Add item to "${action}" column.`, "");
      if(!!itemPrompt) state[2].items.push(itemPrompt);
      return state; 

    default:
      return state;
  }
}

function App() {
  // Task 3: Set up state with the use reducer hook
  // code goes here
  const [item, dispatch] = useReducer(reducer, initialState);
  console.info("item in app >>> ", item);
  return (
    <div className="App">
      <h1>Retrospective Board</h1>
      <div className="columns">
        {/* Task 4: Render column components and pass state,dispatch down */}
        {/* Code goes here*/}
        <Column state={item} dispatch={dispatch} />
        {/* {item.map((obj, index) => (
          <Column key={index} state={item} dispatch={dispatch} />
        ))} */}
      </div>
    </div>
  );
}

export default App;
