import React, { useReducer } from 'react';

function voteReducer(state, action) {
  switch (action) {
    case "UPVOTE":
      return state += action.value
    default: return state;
  }
}

function Item({ item, index, columnKey, dispatch }) {

  const [vote, voteDispatch] = useReducer(voteReducer, 0);

  return (
    // Task 8): Render Item
    <>
        <h4>{item}</h4>
        {/* Task 8a): Render button with upvoteItem dispatch */}
        <button onClick={() => dispatch()}>Upvote</button>
        {/* Task 8b): Render button with deleteeItem dispatch */}
        <button onClick={() => dispatch()}>Delete</button>
    </>
  );
}

export default Item;
