  console.info("item in app >>> ", item);
  return (
    <div className="App">
      <h1>Retrospective Board</h1>
      <div className="columns">
        {/* Task 4: Render column components and pass state,dispatch down */}
        {/* Code goes here*/}
        {item.map((obj, index) => (
          <Column
            key={index}
            columnKey={index}
            title={obj.title}
            items={obj.items}
            dispatch={dispatch}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
