import React, { useReducer } from "react";
import "./App.css";
import Column from "./components/Column";

const titles = ["What went well", "What can be improved", "Action items"];
// const items = [];

// Task 1: Create initial state
const initialState = [
  // Code goes here
  {title: titles[0]}
];

// Task 2: Define reducer function
function reducer(state, action) {
  switch (action) {
    // Code goes here
    case titles[0]:
      let itemPrompt = prompt(`Add item to "${action}" column.`, "");
      console.info("itemPrompt >>> ", itemPrompt); 
      if(!!itemPrompt ) {
        state.items.push(itemPrompt);
        console.info("state >>> ", state); 
      }
      break;

    case titles[1]:
      window.prompt(`Add item to "${action}" column.`);
      console.info("in case 1");
      break;

    case titles[2]:
      window.prompt(`Add item to "${action}" column.`);
      console.info("in case 2");
      break;

    default:
      return state;
  }
}

function App() {
  // Task 3: Set up state with the use reducer hook
  // code goes here
  const [item, dispatch] = useReducer(reducer, initialState);

  return (
    <div className="App">
      <h1>Retrospective Board</h1>
      <div className="columns">
        {/* Task 4: Render column components and pass state,dispatch down */}
        {/* Code goes here*/}
        {titles.map((title, index) => (
          <Column key={index} title={title} dispatch={dispatch} />
        ))}
      </div>
    </div>
  );
}

export default App;
